class Terminal{constructor(t,s){this.container=t,this.lineCount=s,this.lines=[],this.chars="qwertyuiop[]{}asdfghjkl;:'|zxcvbnm?/1234567890-=+",this.iterCount=150,this.chance=.8}start(){this.container.classList.add("terminal-wrapper");for(let t=0;t<this.lineCount;t++)this.appendLine();setInterval((()=>this.drawChars()),500)}drawChars(){for(let t of this.lines)t.innerHTML=this.iterChars()}appendLine(){const t=document.createElement("div");t.classList.add("terminal-col"),this.lines.push(t),this.container.appendChild(t)}iterChars(){let t="";for(let s=0;s<this.iterCount;s++){const s=Math.random(),i=this.chars[Math.randInt(0,this.chars.length-1)];s>this.chance&&(t+=`<span class='${i}'>`),t+=i,s>this.chance&&(t+="</span>")}return t}}
